import React, { useState, useRef } from 'react';
import { View, Text, StyleSheet, Button } from 'react-native';
import CameraComponent from './CameraComponent'; // Ensure this path is correct

const App = () => {
  const cameraRef = useRef(null);
  // Function to handle voice commands
  const handleVoiceCommand = (command) => {
    // Process and perform the action
    console.log(command);
  };

  // In your App.js
const takePicture = async () => {
  if (cameraRef.current) {
    let photo = await cameraRef.current.takePictureAsync({ base64: true });
    console.log(photo.base64); // Add this line to log the base64 string
    const base64Image = photo.base64;

    // Send the image to your backend
    fetch('http://128.189.75.66:5000/process-image', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        image: base64Image
      })
    })
    .then(response => response.json())
    .then(data => {
      // This is where you will handle the response from the backend
      console.log(data); // Log the response from the backend
    })
    .catch(error => {
      console.error('Error:', error);
    });
  }
};


  // The render function
  return (
    <View style={styles.container}>
      <Text>Welcome to VisionPlus</Text>
      <CameraComponent cameraRef={cameraRef} />
      <Button
        title="Take Photo"
        onPress={takePicture}
      />
      <Button
        title="Speak Command"
        onPress={() => handleVoiceCommand('example command')}
      />
    </View>
  );
};

// StyleSheet for your components
const styles = StyleSheet.create({
  container: {
    flex: 1,
    width: '100%',
    height: '100%',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  // Other styles as needed
});

export default App;
